    const [profileName, setProfileName] = useState()
    let platform = "psn";
    let region = "us";
    var profile = profileName
    const handleChange = (e) => {

        setProfileName(e.target.value)
        e.preventDefault();
    }
    const allStatsURL = `https://ow-api.com/v1/stats/${platform}/${region}/${profile}/complete`;

    useEffect(() => {


      const getData = async () => {
        const res = await axios.get(allStatsURL);

        setSecondProfile(res.data);
        setLoading(false);

      }
      getData()

    }, []);
    const handleSubmit = (e) => {
        e.preventDefault()
    }

    console.log(profile)
