      return <h1> Loading</h1>;
    } else {

      return (
          <div>
           <label for="pro2search">Profile 2: </label>
          <input type="search" id="pro2search" onChange= {handleChange} placeholder='who are you looking for?' value = {profile}/> 
          </div>
      )
    }
  }

export default Secondprofile;
