import React, { useState, useEffect } from "react";
import axios from "axios";

function Secondprofile({secondProfile, setSecondProfile}) {
    const [loading, setLoading] = useState(true);
    const [profileName, setProfileName] = useState('')
    let platform = "pc";
    let region = "us";




    useEffect(() => {

        const allStatsURL = `https://ow-api.com/v1/stats/${platform}/${region}/${profileName}/complete`;
      const getData = async () => {
        const res = await axios.get(allStatsURL);

        setSecondProfile(res.data);
        setLoading(false)
      }
      getData()

    }, []);
    const handleSubmit = (e) => {


        e.preventDefault()
    }
    const handleChange = (e) => {

        setProfileName(e.target.value)
        e.preventDefault();
    }


    if (loading) {
      return (
      <h1> loading</h1>
      )
    }else{
      return (
          <div>
          <form onSubmit= {handleSubmit}>
           <label for="pro2search">Profile 2: </label>
          <input type="search" id="pro2search" onChange= {handleChange} placeholder='who are you looking for?' value = {profileName}/> 
          </form>

          <div> <h2>{secondProfile.rating} </h2></div>
          </div>

      )
    }
  }

export default Secondprofile;
