    <div className= "first-profile">
      <form onSubmit={handleSubmit}>
        <label for="pro2search">Profile 1: </label>
        <input
          type="search"
          id="pro2search"
          onChange={handleChange}
          placeholder="who are you looking for?"
          value={profileName}
        />
      </form>
<button id= "pc" onClick = {handleChangePc} > PC </button>
<button id = "psn" onClick = {handleChangePsn}> PSN</button>
<button id = "xbox"> XBOX </button>
      <Firstresult firstProfile={firstProfile} loading={loading} />
    </div>
  );
}

export default Firstprofile;
